import { BrowserRouter, Routes, Route, NavLink, useLocation, useNavigate } from "react-router-dom";
import { useEffect, useState } from "react";

/* ---------- AUTH ---------- */
const authHeader = {
  Authorization: "Basic " + btoa("admin:admin123"),
  "Content-Type": "application/json"
};

/* ---------- UTILS ---------- */
const fetchJson = (url, setter) => {
  fetch(url, { headers: authHeader })
    .then(res => res.json())
    .then(setter)
    .catch(console.error);
};

const formatUptime = (seconds = 0) => {
  const hrs = Math.floor(seconds / 3600);
  const mins = Math.floor((seconds % 3600) / 60);
  const secs = seconds % 60;
  return `${hrs.toString().padStart(2, "0")}:${mins.toString().padStart(2, "0")}:${secs
    .toString()
    .padStart(2, "0")}`;
};

/* ---------- NAV CONFIG ---------- */
const tabs = [
  { path: "/", label: "Home", icon: "üè†" },
  { path: "/health", label: "Health", icon: "ü©∫" },
  { path: "/app", label: "App", icon: "üì¶" },
  { path: "/version", label: "Version", icon: "üîñ" },
  { path: "/metrics", label: "Metrics", icon: "üìä" },
  { path: "/uptime", label: "Uptime", icon: "‚è±" }
];

/* ---------- COMPONENTS ---------- */

const Navigation = () => {
  const navigate = useNavigate();
  const location = useLocation();

  // Keyboard navigation
  useEffect(() => {
    const handler = e => {
      const index = tabs.findIndex(t => t.path === location.pathname);
      if (e.key === "ArrowRight" && index < tabs.length - 1) {
        navigate(tabs[index + 1].path);
      }
      if (e.key === "ArrowLeft" && index > 0) {
        navigate(tabs[index - 1].path);
      }
    };
    window.addEventListener("keydown", handler);
    return () => window.removeEventListener("keydown", handler);
  }, [location.pathname, navigate]);

  return (
    <nav style={styles.navBar}>
      {tabs.map(tab => (
        <NavLink
          key={tab.path}
          to={tab.path}
          end
          style={({ isActive }) => ({
            ...styles.navItem,
            ...(isActive ? styles.navItemActive : {})
          })}
        >
          <span style={styles.icon}>{tab.icon}</span>
          <span className="label">{tab.label}</span>
        </NavLink>
      ))}
    </nav>
  );
};

const Breadcrumbs = () => {
  const location = useLocation();
  const current = tabs.find(t => t.path === location.pathname);
  return (
    <div style={styles.breadcrumbs}>
      Home {current && current.path !== "/" && ` / ${current.label}`}
    </div>
  );
};

const DataTable = ({ data }) => (
  <table style={styles.table}>
    <tbody>
      {Object.entries(data || {}).map(([k, v]) => (
        <tr key={k}>
          <td style={styles.key}>{k}</td>
          <td style={styles.value}>{String(v)}</td>
        </tr>
      ))}
    </tbody>
  </table>
);

/* ---------- PAGES ---------- */

const Home = () => (
  <>
    <h2>üöÄ Hello Server Dashboard</h2>
    <p style={{ color: "#475569" }}>
      Use tabs or ‚Üê ‚Üí arrow keys to navigate.
    </p>
  </>
);

const Health = () => {
  const [data, setData] = useState({});
  useEffect(() => fetchJson("http://localhost:8080/health", setData), []);
  return <><h2>ü©∫ Health</h2><DataTable data={data} /></>;
};

const AppInfo = () => {
  const [data, setData] = useState({});
  useEffect(() => fetchJson("http://localhost:8080/app", setData), []);
  return <><h2>üì¶ App Info</h2><DataTable data={data} /></>;
};

const Version = () => {
  const [data, setData] = useState({});
  useEffect(() => fetchJson("http://localhost:8080/version", setData), []);
  return <><h2>üîñ Version</h2><DataTable data={data} /></>;
};

const Metrics = () => {
  const [data, setData] = useState({});
  useEffect(() => fetchJson("http://localhost:8080/metrics", setData), []);
  return <><h2>üìä Metrics</h2><DataTable data={data} /></>;
};

const Uptime = () => {
  const [seconds, setSeconds] = useState(0);

  useEffect(() => {
    const load = () =>
      fetch("http://localhost:8080/uptime", { headers: authHeader })
        .then(res => res.json())
        .then(d => setSeconds(d.uptimeSeconds));

    load();
    const i = setInterval(load, 5000);
    return () => clearInterval(i);
  }, []);

  return (
    <>
      <h2>‚è± Server Uptime</h2>
      <div style={styles.uptimeBox}>{formatUptime(seconds)}</div>
    </>
  );
};

/* ---------- APP ---------- */

export default function App() {
  return (
    <BrowserRouter>
      <div style={styles.page}>
        <div style={styles.card}>
          <Navigation />
          <Breadcrumbs />
          <div style={styles.content}>
            <Routes>
              <Route path="/" element={<Home />} />
              <Route path="/health" element={<Health />} />
              <Route path="/app" element={<AppInfo />} />
              <Route path="/version" element={<Version />} />
              <Route path="/metrics" element={<Metrics />} />
              <Route path="/uptime" element={<Uptime />} />
            </Routes>
          </div>
        </div>
      </div>
    </BrowserRouter>
  );
}

/* ---------- STYLES ---------- */

const styles = {
  page: {
    minHeight: "100vh",
    background: "linear-gradient(135deg, #667eea, #764ba2)",
    display: "flex",
    justifyContent: "center",
    paddingTop: "30px",
    fontFamily: "Segoe UI, sans-serif"
  },
  card: {
    background: "#fff",
    width: "100%",
    maxWidth: "960px",
    borderRadius: "16px",
    boxShadow: "0 25px 50px rgba(0,0,0,0.25)",
    overflow: "hidden"
  },
  navBar: {
    position: "sticky",
    top: 0,
    display: "flex",
    gap: "6px",
    padding: "12px",
    background: "#f8fafc",
    borderBottom: "1px solid #e5e7eb",
    zIndex: 10,
    flexWrap: "wrap"
  },
  navItem: {
    display: "flex",
    alignItems: "center",
    gap: "6px",
    padding: "8px 14px",
    borderRadius: "10px",
    textDecoration: "none",
    color: "#334155",
    fontWeight: 500,
    fontSize: "14px"
  },
  navItemActive: {
    background: "#3b82f6",
    color: "#fff"
  },
  icon: {
    fontSize: "16px"
  },
  breadcrumbs: {
    padding: "10px 20px",
    fontSize: "13px",
    color: "#64748b",
    background: "#f1f5f9"
  },
  content: {
    padding: "24px"
  },
  table: {
    width: "100%",
    borderCollapse: "collapse",
    marginTop: "16px"
  },
  key: {
    padding: "12px",
    fontWeight: "bold",
    borderBottom: "1px solid #e5e7eb",
    background: "#f8fafc",
    width: "40%"
  },
  value: {
    padding: "12px",
    borderBottom: "1px solid #e5e7eb"
  },
  uptimeBox: {
    marginTop: "20px",
    padding: "20px",
