export const authHeader = {
  Authorization: "Basic " + btoa("admin:admin123"),
  "Content-Type": "application/json"
};

export const fetchJson = (url, setter) => {
  fetch(url, { headers: authHeader })
    .then(res => res.json())
    .then(setter)
    .catch(console.error);
};
