import { useEffect, useState } from "react";

function App() {
  const [time, setTime] = useState("");
  const [counter, setCounter] = useState("");
  const [postResponse, setPostResponse] = useState("");

  const authHeader = {
    "Authorization": "Basic " + btoa("admin:admin123"),
    "Content-Type": "application/json"
  };

  useEffect(() => {
    fetch("http://localhost:8080/time", { headers: authHeader })
      .then(res => res.text())
      .then(setTime);
  }, []);

  const hitCounter = () => {
    fetch("http://localhost:8080/counter", { headers: authHeader })
      .then(res => res.text())
      .then(setCounter);
  };

  const sendPost = () => {
    fetch("http://localhost:8080/echo", {
      method: "POST",
      headers: authHeader,
      body: JSON.stringify({ message: "Hello from React UI ðŸš€" })
    })
      .then(res => res.text())
      .then(setPostResponse);
  };

  return (
    <div style={styles.page}>
      <div style={styles.card}>
        <h1 style={styles.title}>ðŸš€ Hello Server Dashboard</h1>

        <div style={styles.section}>
          <h3>ðŸ•’ Server Time</h3>
          <p style={styles.value}>{time}</p>
        </div>

        <div style={styles.section}>
          <h3>ðŸ”¢ Counter</h3>
          <button style={styles.buttonBlue} onClick={hitCounter}>
            Hit Counter
          </button>
          <p style={styles.value}>{counter}</p>
        </div>

        <div style={styles.section}>
          <h3>ðŸ“¦ POST API</h3>
          <button style={styles.buttonGreen} onClick={sendPost}>
            Send POST Request
          </button>
          {postResponse && (
            <p style={styles.response}>{postResponse}</p>
          )}
        </div>

        <hr />

        <div style={styles.links}>
          <a href="http://localhost:8080/hello" target="_blank">/hello</a>
          <a href="http://localhost:8080/health" target="_blank">/health</a>
          <a href="http://localhost:8080/app" target="_blank">/app</a>
        </div>
      </div>
    </div>
  );
}

const styles = {
  page: {
    minHeight: "100vh",
    background: "linear-gradient(135deg, #667eea, #764ba2)",
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    fontFamily: "Segoe UI, sans-serif"
  },
  card: {
    background: "#ffffff",
    padding: "30px",
    borderRadius: "12px",
    width: "420px",
    boxShadow: "0 15px 30px rgba(0,0,0,0.2)"
  },
  title: {
    textAlign: "center",
    marginBottom: "20px",
    color: "#333"
  },
  section: {
    marginBottom: "20px"
  },
  value: {
    fontSize: "16px",
    fontWeight: "bold",
    color: "#444"
  },
  response: {
    marginTop: "10px",
    padding: "10px",
    background: "#f0fdf4",
    border: "1px solid #22c55e",
    borderRadius: "6px",
    color: "#166534"
  },
  buttonBlue: {
    padding: "10px 16px",
    background: "#3b82f6",
    color: "#fff",
    border: "none",
    borderRadius: "6px",
    cursor: "pointer"
  },
  buttonGreen: {
    padding: "10px 16px",
    background: "#22c55e",
    color: "#fff",
    border: "none",
    borderRadius: "6px",
    cursor: "pointer"
  },
  links: {
    display: "flex",
    justifyContent: "space-around"
  }
};

export default App;
