import { useEffect, useState } from "react";

function App() {
  const [time, setTime] = useState("");
  const [counter, setCounter] = useState("");
  const [postResponse, setPostResponse] = useState("");
  const [uptime, setUptime] = useState(0);
  const [darkMode, setDarkMode] = useState(false);
  const [tick, setTick] = useState(false);

  const authHeader = {
    Authorization: "Basic " + btoa("admin:admin123"),
    "Content-Type": "application/json"
  };

  // Auto refresh time every 5 seconds
  useEffect(() => {
    fetchTime();
    const interval = setInterval(() => {
      fetchTime();
      setUptime(prev => prev + 5);
      setTick(prev => !prev);
    }, 5000);

    return () => clearInterval(interval);
  }, []);

  const fetchTime = () => {
    fetch("http://localhost:8080/time", { headers: authHeader })
      .then(res => res.text())
      .then(setTime)
      .catch(console.error);
  };

  const hitCounter = () => {
    fetch("http://localhost:8080/counter", { headers: authHeader })
      .then(res => res.text())
      .then(setCounter)
      .catch(console.error);
  };

  const sendPost = () => {
    fetch("http://localhost:8080/echo", {
      method: "POST",
      headers: authHeader,
      body: JSON.stringify({ message: "Hello from React UI üöÄ" })
    })
      .then(res => res.text())
      .then(setPostResponse)
      .catch(console.error);
  };

  const formatTime = iso => {
    if (!iso) return null;
    const d = new Date(iso);
    return {
      day: d.toLocaleDateString("en-IN", {
        weekday: "long",
        day: "numeric",
        month: "long",
        year: "numeric"
      }),
      time: d.toLocaleTimeString("en-IN", {
        hour: "2-digit",
        minute: "2-digit",
        second: "2-digit",
        hour12: true
      })
    };
  };

  const formatted = formatTime(time);

  const theme = darkMode ? darkStyles : lightStyles;

  return (
    <div style={theme.page}>
      <div style={theme.card}>
        <div style={theme.header}>
          <h1>üöÄ Hello Server Dashboard</h1>
          <button style={theme.toggle} onClick={() => setDarkMode(!darkMode)}>
            {darkMode ? "‚òÄ Light" : "üåô Dark"}
          </button>
        </div>

        <div style={theme.section}>
          <h3>üïí Server Time</h3>
          {formatted && (
            <div style={theme.timeBox}>
              <div>{formatted.day}</div>
              <div
                style={{
                  ...theme.timeClock,
                  transform: tick ? "scale(1.03)" : "scale(1)"
                }}
              >
                {formatted.time}
              </div>
              <div style={theme.sub}>IST ‚Ä¢ Auto refresh 5s</div>
            </div>
          )}
        </div>

        <div style={theme.section}>
          <h3>‚è± Server Uptime</h3>
          <p>{uptime} seconds</p>
        </div>

        <div style={theme.section}>
          <h3>üî¢ Counter</h3>
          <button style={theme.buttonBlue} onClick={hitCounter}>
            Hit Counter
          </button>
          <p>{counter}</p>
        </div>

        <div style={theme.section}>
          <h3>üì¶ POST API</h3>
          <button style={theme.buttonGreen} onClick={sendPost}>
            Send POST
          </button>
          {postResponse && <p style={theme.response}>{postResponse}</p>}
        </div>

        <hr />

        <div style={theme.links}>
          <a href="http://localhost:8080/hello" target="_blank" rel="noreferrer">/hello</a>
          <a href="http://localhost:8080/health" target="_blank" rel="noreferrer">/health</a>
          <a href="http://localhost:8080/app" target="_blank" rel="noreferrer">/app</a>
        </div>
      </div>
    </div>
  );
}

/* ---------- THEMES ---------- */

const base = {
  page: {
    minHeight: "100vh",
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    fontFamily: "Segoe UI, sans-serif",
    transition: "all 0.3s ease"
  },
  card: {
    padding: "28px",
    borderRadius: "16px",
    width: "460px",
    transition: "all 0.3s ease"
  },
  header: {
    display: "flex",
    justifyContent: "space-between",
    alignItems: "center"
  },
  section: {
    marginTop: "20px"
  },
  timeBox: {
    padding: "14px",
    borderRadius: "10px",
    textAlign: "center"
  },
  timeClock: {
    fontSize: "26px",
    fontWeight: "bold",
    transition: "transform 0.3s ease"
  },
  sub: {
    fontSize: "12px",
    marginTop: "4px"
  },
  buttonBlue: {
    padding: "10px 18px",
    background: "#3b82f6",
    color: "#fff",
    border: "none",
    borderRadius: "8px",
    cursor: "pointer"
  },
  buttonGreen: {
    padding: "10px 18px",
    background: "#22c55e",
    color: "#fff",
    border: "none",
    borderRadius: "8px",
    cursor: "pointer"
  },
  response: {
    marginTop: "10px",
    padding: "10px",
    borderRadius: "8px"
  },
  links: {
    display: "flex",
    justifyContent: "space-between"
  },
  toggle: {
    border: "none",
    padding: "6px 10px",
    borderRadius: "8px",
    cursor: "pointer"
  }
};

const lightStyles = {
  ...base,
  page: { ...base.page, background: "linear-gradient(135deg, #667eea, #764ba2)" },
  card: { ...base.card, background: "#fff", boxShadow: "0 20px 40px rgba(0,0,0,0.25)" },
  response: { ...base.response, background: "#ecfdf5", border: "1px solid #22c55e" },
  toggle: { ...base.toggle, background: "#e5e7eb" }
};

const darkStyles = {
  ...base,
  page: { ...base.page, background: "#0f172a" },
  card: { ...base.card, background: "#020617", color: "#e5e7eb", boxShadow: "0 20px 40px rgba(0,0,0,0.8)" },
  response: { ...base.response, background: "#022c22", border: "1px solid #22c55e" },
  toggle: { ...base.toggle, background: "#334155", color: "#fff" }
};

export default App;
