import { useEffect, useState } from "react";

function App() {
  const [time, setTime] = useState("");
  const [counter, setCounter] = useState("");

  useEffect(() => {
    fetch("http://localhost:8080/time")
      .then(res => res.text())
      .then(data => setTime(data))
      .catch(err => console.error(err));
  }, []);

  const hitCounter = () => {
    fetch("http://localhost:8080/counter")
      .then(res => res.text())
      .then(data => setCounter(data))
      .catch(err => console.error(err));
  };

  return (
    <div style={{ padding: 40, fontFamily: "Arial" }}>
      <h1>Hello Server ðŸš€</h1>

      <p><b>Server Time</b></p>
      <p>{time}</p>

      <button onClick={hitCounter}>Hit Counter</button>
      <p>Counter value: {counter}</p>

      <hr />

      <a href="http://localhost:8080/hello">/hello</a><br />
      <a href="http://localhost:8080/health">/health</a><br />
      <a href="http://localhost:8080/app">/app</a>
    </div>
  );
}

export default App;
