import { BrowserRouter, Routes, Route, NavLink } from "react-router-dom";
import { useEffect, useState } from "react";

/* ---------- AUTH ---------- */
const authHeader = {
  Authorization: "Basic " + btoa("admin:admin123"),
  "Content-Type": "application/json"
};

/* ---------- UTILS ---------- */
const fetchJson = (url, setter) => {
  fetch(url, { headers: authHeader })
    .then(res => res.json())
    .then(setter)
    .catch(console.error);
};

const formatUptime = (seconds = 0) => {
  const hrs = Math.floor(seconds / 3600);
  const mins = Math.floor((seconds % 3600) / 60);
  const secs = seconds % 60;
  return `${hrs.toString().padStart(2, "0")}:${mins
    .toString()
    .padStart(2, "0")}:${secs.toString().padStart(2, "0")}`;
};

/* ---------- NAV COMPONENT ---------- */
const Navigation = () => {
  const tabs = [
    { path: "/", label: "Home" },
    { path: "/health", label: "Health" },
    { path: "/app", label: "App" },
    { path: "/version", label: "Version" },
    { path: "/metrics", label: "Metrics" },
    { path: "/uptime", label: "Uptime" }
  ];

  return (
    <nav style={styles.navBar}>
      {tabs.map(tab => (
        <NavLink
          key={tab.path}
          to={tab.path}
          end
          style={({ isActive }) => ({
            ...styles.navItem,
            ...(isActive ? styles.navItemActive : {})
          })}
        >
          {tab.label}
        </NavLink>
      ))}
    </nav>
  );
};

/* ---------- TABLE ---------- */
const DataTable = ({ data }) => (
  <table style={styles.table}>
    <tbody>
      {Object.entries(data || {}).map(([k, v]) => (
        <tr key={k}>
          <td style={styles.key}>{k}</td>
          <td style={styles.value}>{String(v)}</td>
        </tr>
      ))}
    </tbody>
  </table>
);

/* ---------- PAGES ---------- */

const Home = () => (
  <>
    <h2>üöÄ Hello Server Dashboard</h2>
    <p style={{ color: "#475569" }}>
      Use the navigation tabs above to explore system details.
    </p>
  </>
);

const Health = () => {
  const [data, setData] = useState({});
  useEffect(() => fetchJson("http://localhost:8080/health", setData), []);
  return (
    <>
      <h2>ü©∫ Health</h2>
      <DataTable data={data} />
    </>
  );
};

const AppInfo = () => {
  const [data, setData] = useState({});
  useEffect(() => fetchJson("http://localhost:8080/app", setData), []);
  return (
    <>
      <h2>üì¶ App Info</h2>
      <DataTable data={data} />
    </>
  );
};

const Version = () => {
  const [data, setData] = useState({});
  useEffect(() => fetchJson("http://localhost:8080/version", setData), []);
  return (
    <>
      <h2>üîñ Version</h2>
      <DataTable data={data} />
    </>
  );
};

const Metrics = () => {
  const [data, setData] = useState({});
  useEffect(() => fetchJson("http://localhost:8080/metrics", setData), []);
  return (
    <>
      <h2>üìä Metrics</h2>
      <DataTable data={data} />
    </>
  );
};

const Uptime = () => {
  const [seconds, setSeconds] = useState(0);

  useEffect(() => {
    fetch("http://localhost:8080/uptime", { headers: authHeader })
      .then(res => res.json())
      .then(data => setSeconds(data.uptimeSeconds));

    const interval = setInterval(() => {
      fetch("http://localhost:8080/uptime", { headers: authHeader })
        .then(res => res.json())
        .then(data => setSeconds(data.uptimeSeconds));
    }, 5000);

    return () => clearInterval(interval);
  }, []);

  return (
    <>
      <h2>‚è± Server Uptime</h2>
      <div style={styles.uptimeBox}>{formatUptime(seconds)}</div>
    </>
  );
};

/* ---------- APP ---------- */

export default function App() {
  return (
    <BrowserRouter>
      <div style={styles.page}>
        <div style={styles.card}>
          <Navigation />
          <div style={styles.content}>
            <Routes>
              <Route path="/" element={<Home />} />
              <Route path="/health" element={<Health />} />
              <Route path="/app" element={<AppInfo />} />
              <Route path="/version" element={<Version />} />
              <Route path="/metrics" element={<Metrics />} />
              <Route path="/uptime" element={<Uptime />} />
            </Routes>
          </div>
        </div>
      </div>
    </BrowserRouter>
  );
}

/* ---------- STYLES ---------- */

const styles = {
  page: {
    minHeight: "100vh",
    background: "linear-gradient(135deg, #667eea, #764ba2)",
    display: "flex",
    justifyContent: "center",
    alignItems: "flex-start",
    paddingTop: "40px",
    fontFamily: "Segoe UI, sans-serif"
  },
  card: {
    background: "#ffffff",
    width: "900px",
    borderRadius: "16px",
    boxShadow: "0 25px 50px rgba(0,0,0,0.25)",
    overflow: "hidden"
  },
  navBar: {
    display: "flex",
    gap: "8px",
    padding: "14px",
    background: "#f8fafc",
    borderBottom: "1px solid #e5e7eb"
  },
  navItem: {
    padding: "8px 14px",
    borderRadius: "10px",
    textDecoration: "none",
    color: "#334155",
    fontWeight: 500,
    transition: "all 0.2s ease"
  },
  navItemActive: {
    background: "#3b82f6",
    color: "#ffffff",
    boxShadow: "0 6px 16px rgba(59,130,246,0.4)"
  },
  content: {
    padding: "24px"
  },
  table: {
    width: "100%",
    borderCollapse: "collapse",
    marginTop: "16px"
  },
  key: {
    padding: "12px",
    fontWeight: "bold",
    borderBottom: "1px solid #e5e7eb",
    width: "40%",
    background: "#f8fafc"
  },
  value: {
    padding: "12px",
    borderBottom: "1px solid #e5e7eb"
  },
  uptimeBox: {
    marginTop: "20px",
    padding: "20px",
    fontSize: "28px",
    fontWeight: "bold",
    background: "#f1f5f9",
    borderRadius: "12px",
    textAlign: "center"
  }
};
